});
chart.guide().text({
  position: [ '50%', '50%' ],
  content: '85%',
  style: {
    fill: '#1890FF'
  }
});
chart.interval()
  .position('x*y')
  .size(20)
  .animate({
    appear: {
      duration: 1200,
      easing: 'cubicIn'
    }
  });
chart.render();

