  value: 6,
  type: '实际收益率'
}, {
  time: '2016-08-09 00:35:00',
  value: -12,
  type: '实际收益率'
}, {
  time: '2016-08-09 01:00:00',
  value: 1,
  type: '实际收益率'
}, {
  time: '2016-08-09 01:20:00',
  value: 9,
  type: '实际收益率'
}, {
  time: '2016-08-10 01:40:00',
  value: 13,
  type: '实际收益率'
}, {
  time: '2016-08-10 02:00:00',
  value: -3,
  type: '实际收益率'
}, {
  time: '2016-08-10 02:20:00',
  value: 11,
