  }
});
chart.legend({
  position: 'bottom',
  offsetY: -5
});
chart.line()
  .position('time*value')
  .color('type')
  .shape('type', function(type) {
    if (type === '预期收益率') {
      return 'line';
    }
    if (type === '实际收益率') {
      return 'dash';
    }
  });

chart.render();

