import F2 from '@antv/f2';
import insertCss from 'insert-css';

insertCss(`
  canvas.steps-record {
    border-radius: 5px;
    background-image: linear-gradient(-135deg, #874BFF 0%, #6854EE 43%, #6052F2 63%, #534FFA 100%);
  }
  .chart-tooltip {
    position: absolute;
    z-index: 99;
    font-size: 12px;
    color: #ADC6FF;
    text-align: center;
    top: 38vw;
    left: 0;
    margin-top: 3px;
    visibility: hidden;
    transition: top 0.4s cubic-bezier(0.23, 1, 0.32, 1)
  }
`);
// 设置独立的css
const container = document.getElementById('container');
container.className = 'steps-record';
$(container).after('<div class="chart-tooltip" id="tooltip"></div>');

const data = [{
  date: '2018-04-21',
  steps: 59
}, {
  date: '2018-04-22',
  steps: 2515
}, {
  date: '2018-04-23',
  steps: 6524
}, {
  date: '2018-04-24',
  steps: 26044
}, {
  date: '2018-04-25',
  steps: 29763
}, {
  date: '2018-04-26',
