      start: [ 'min', 25 ],
      end: [ 'max', 25 ],
      style: {
        stroke: '#d0502d',
        lineWidth: 2,
        lineCap: 'round'
      }
    });

    chart.line().position('month*PM');
    chart.render();
  });

