import F2 from '@antv/f2';

const data = [{
  time: 'Jan.',
  tem: 1000
}, {
  time: 'Feb.',
  tem: 2200
}, {
  time: 'Mar.',
  tem: 2000
}, {
  time: 'Apr.',
  tem: 2600
}, {
  time: 'May.',
  tem: 2000
}, {
  time: 'Jun.',
  tem: 2600
}, {
  time: 'Jul.',
  tem: 2800
}, {
  time: 'Aug.',
  tem: 2000
}];
const chart = new F2.Chart({
  id: 'container',
  pixelRatio: window.devicePixelRatio
});

