  value: 403836,
  year: '2016'
}, {
  time: 'Aug',
  value: 334281,
  year: '2016'
}, {
  time: 'Sep',
  value: 329221,
  year: '2016'
}, {
  time: 'Oct',
  value: 378278,
  year: '2016'
}, {
  time: 'Nov',
  value: 454534,
  year: '2016'
}, {
  time: 'Dec',
  value: 573530,
  year: '2016'
}];
const chart = new F2.Chart({
  id: 'container',
  pixelRatio: window.devicePixelRatio
