          var value = currData[type];
          var color;

          if (type === 'volumn') {
            color = '#FFF';
          } else {
            color = currData.trend === 0 ? COLOR_MAP[0] : COLOR_MAP[1];
          }

          $(ele).css({
            color: color
          });
          $(ele).text(value);
        });
        $('#kTooltip').css('visibility', 'visible');
        updateMATip(data, allDates.indexOf(currData.reportDate));
      },
      onHide: function onHide() {
        $('#kTooltip').css('visibility', 'hidden');
        $('#ma').css('visibility', 'hidden');
      }
    });
    chart.schema().position('reportDate*range').color('trend', function (val) {
      return COLOR_MAP[val];
    }).shape('candle');
    chart.line().position('reportDate*MA5').color('#C6268B').size(1).animate(false);
    chart.line().position('reportDate*MA10').color('#D7A429').size(1).animate(false);
    chart.line().position('reportDate*MA20').color('#227BAF').size(1).animate(false);
    chart.line().position('reportDate*MA30').color('#6960C4').size(1).animate(false);
    chart.interaction('pinch', {
      maxScale: 25,
      onProcess: function onProcess() {
        if (this.pressed) return;
        var currentValues = chart.getXScale().values; // 获取平移后的当前展示 values

        barChart.scale('reportDate', {
          type: 'timeCat',
          values: currentValues
        });
        barChart.repaint();
      },
      onEnd: function onEnd() {
        barChart.hideTooltip();
        chart.hideTooltip();
      }
    });
    chart.interaction('pan', {
      onProcess: function onProcess() {
        if (this.pressed) return; // TODO, 这个标识位好恶心

        var currentValues = chart.getXScale().values; // 获取平移后的当前展示 values

        barChart.scale('reportDate', {
          type: 'timeCat',
          values: currentValues
        });
        barChart.repaint();
      },
      onEnd: function onEnd() {
        barChart.hideTooltip();
        chart.hideTooltip();
      }
    });
    chart.render();
    return chart;
  }
});

