      label: function label(text, index, total) {
        var cfg = {
          fill: '#818991',
          textAlign: 'center',
          text: text
        };

        if (index === 0) {
          cfg.textAlign = 'start';
        } else if (index === total - 1) {
          cfg.textAlign = 'end';
        } else {
          cfg.text += '/1300';
        }

        return cfg;
      },
      line: {
        stroke: '#181D26'
      },
      grid: {
        lineWidth: 1,
        stroke: '#181D26'
      }
    });
    chart.axis('price', {
      line: {
        stroke: '#181D26'
      },
      grid: {
        stroke: '#181D26'
      },
      labelOffset: -2,
      label: function label(text, index, total) {
        var cfg = {
          textAlign: 'start',
          text: text,
          fill: '#818991'
        };

        if (index === 0) {
          cfg.textBaseline = 'bottom';
        } else if (index === total - 1) {
          cfg.textBaseline = 'top';
        }

        return cfg;
      }
    });
    chart.tooltip({
      alwaysShow: true,
      showCrosshairs: true,
      crosshairsType: 'xy',
      showYTip: true,
      crosshairsStyle: {
        stroke: '#D1D3D4',
        lineWidth: 1
      },
      yTip: function yTip(val) {
        return {
          fill: '#80888F',
          fontSize: 10,
          text: val.toFixed(2)
        };
      },
      yTipBackground: {
        fill: '#232C39',
        fillOpacity: 0.95,
        radius: 2,
        stroke: '#252F3D'
      },
      custom: true,
      onChange: function onChange(obj) {
        var currentPoint = {
          x: obj.x,
          y: obj.y
        };
        barChart.showTooltip(currentPoint);
        var data = obj.items[0].origin;
        $('#tooltip .item-value').each(function (index, ele) {
          var type = $(ele).data('type');
          var value = data[type];
          var color = data.trend === 0 ? COLOR_MAP[0] : COLOR_MAP[1];

          if (type === 'volumn') {
            color = '#FFF';
            value = data.text + ' 手';
          } else if (type === 'time') {
            color = '#FFF';
          } else if (type === 'rate') {
            // 涨跌幅度
            value = (data.price - BASIC_PRICE) / BASIC_PRICE;
            value = (value * 100).toFixed(2) + '%';
          } else if (type === 'amount') {
            value = (data.price - BASIC_PRICE).toFixed(2);
          } else if (type === 'price') {
            value = value.toFixed(2);
          }

          $(ele).css({
            color: color
          });
          $(ele).text(value);
        });
        $('#tooltip').css('visibility', 'visible');
      },
      onHide: function onHide() {
        $('#tooltip').css('visibility', 'hidden');
      }
    });
    chart.guide().line({
      start: ['min', BASIC_PRICE],
      end: ['max', BASIC_PRICE],
      style: {
        lineDash: [8],
        stroke: '#728B6E'
      }
    });
    chart.guide().text({
      position: ['min', BASIC_PRICE],
      content: BASIC_PRICE,
      style: {
        fill: '#878F92',
        fontSize: 10,
        fontWeight: 'bold',
        textAlign: 'start',
        textBaseline: 'bottom'
      }
    });
    chart.line().position('time*price').color('#98BCDE');
    chart.area().position('time*price').color('#98BCDE');
    chart.render();
    return chart;
  }
});

