  value: 5.6,
  cate: '20-29'
}, {
  gender: 'Female',
  value: 6.0,
  cate: '30-39'
}, {
  gender: 'Female',
  value: 7.2,
  cate: '40-49'
}, {
  gender: 'Female',
  value: 7.9,
  cate: '50-59'
